<?php        $un = $_SESSION['user_name'];		$query =  <<<EOT			SELECT 			  u.user_name AS user_name			, p.post_tx AS post                        , CONCAT(DATE_FORMAT(h.post_dt,'%b %e'), ', ',DATE_FORMAT(h.post_dt, '%Y'), ' at ', DATE_FORMAT(h.post_dt, '%l:%i %p')) AS date                        			FROM 			  users u			, posts p			, has h			WHERE h.user_id = u.user_id			AND p.guid = h.posts_guid			AND p.purge_dt IS NULL 			AND u.user_name = "$un"					OR					(h.user_id in (						SELECT f.following_id 						FROM following f, users uf						WHERE  f.user_id = u.user_id						AND uf.purge_dt IS NULL					))			ORDER BY h.post_dt DESC 			LIMIT 6EOT;?>